//load buffer character
void loadBuffer(uint8_t value) {
  //load a space, adjust if less LEDS PER COL are used
  for (uint8_t j = 0; j < CHAR_HEIGHT; j++)
  {
    backBuffer[0][j] = 0;
    backBuffer[1][j] = 0;
    backBuffer[2][j] = 0;
    backBuffer[3][j] = 0;
  }

  //load character
  for (uint8_t i = 0; i < (CHAR_WIDTH - OFFSET) * LEDS_PER_COL; i++)
  {
    for (uint8_t j = 0; j < CHAR_HEIGHT; j++)
    {
      backBuffer[i + LEDS_PER_COL][j] = font[value][(i / LEDS_PER_COL)][j];
    }
  }
}

